import { pokeapi } from '@/api/pokeapi';
export default {
  name: 'PokemonSearch',
  data() {
    return {
      pokemonData: {},
      pokemonID: ""
    };
  },
  methods: {
    async searchPokemon() {
      try {
        const pokemonToFind = await fetch(`${pokeapi}/${this.pokemonID}`);
        const pokemon = await pokemonToFind.json();
        this.pokemonData = pokemon;
        console.log(pokemon);
        return pokemon;
      } catch (error) {
        alert("pokemon não encontrado");
      }
    }
  }
};
